import{_ as d,c as e,o,ah as r}from"./chunks/framework.DEXV2HBK.js";const u=JSON.parse('{"title":"浏览器","description":"","frontmatter":{"title":"浏览器","date":"2025-07-09T00:00:00.000Z","category":"浏览器","tags":["浏览器","浏览器事件"]},"headers":[],"relativePath":"posts/前端/浏览器事件.md","filePath":"posts/前端/浏览器事件.md"}'),c={name:"posts/前端/浏览器事件.md"};function a(n,t,h,i,l,s){return o(),e("div",null,t[0]||(t[0]=[r('<p>当然可以！下面是一个<strong>完整、全面的浏览器事件监听列表（包括 DOM 事件、表单事件、窗口事件、触摸事件、媒体事件等）</strong>，适用于现代浏览器（如 Chrome、Firefox、Edge、Safari 等），基于 <strong>WHATWG DOM 标准</strong> 和 <strong>MDN 文档</strong>。</p><hr><h2 id="🧠-说明" tabindex="-1">🧠 说明 <a class="header-anchor" href="#🧠-说明" aria-label="Permalink to &quot;🧠 说明&quot;">​</a></h2><ul><li>这些事件可以通过 <code>addEventListener()</code> 监听。</li><li>所有事件都可绑定在 DOM 元素或 <code>window</code> 上，具体取决于事件类型。</li><li>每个事件都有其适用场景和触发条件。</li></ul><hr><h1 id="📚-完整浏览器事件监听列表-分类整理" tabindex="-1">📚 完整浏览器事件监听列表（分类整理） <a class="header-anchor" href="#📚-完整浏览器事件监听列表-分类整理" aria-label="Permalink to &quot;📚 完整浏览器事件监听列表（分类整理）&quot;">​</a></h1><hr><h2 id="🧱-一、dom-元素相关事件-基础交互" tabindex="-1">🧱 一、DOM 元素相关事件（基础交互） <a class="header-anchor" href="#🧱-一、dom-元素相关事件-基础交互" aria-label="Permalink to &quot;🧱 一、DOM 元素相关事件（基础交互）&quot;">​</a></h2><table tabindex="0"><thead><tr><th>事件名</th><th>描述</th></tr></thead><tbody><tr><td><code>click</code></td><td>单击元素时触发（鼠标左键或触控）</td></tr><tr><td><code>contextmenu</code></td><td>右键菜单弹出前触发</td></tr><tr><td><code>dblclick</code></td><td>双击触发</td></tr><tr><td><code>mousedown</code></td><td>鼠标按键按下</td></tr><tr><td><code>mouseup</code></td><td>鼠标按键释放</td></tr><tr><td><code>mousemove</code></td><td>鼠标移动</td></tr><tr><td><code>mouseover</code></td><td>鼠标移入元素（冒泡）</td></tr><tr><td><code>mouseout</code></td><td>鼠标移出元素（冒泡）</td></tr><tr><td><code>mouseenter</code></td><td>鼠标进入元素（不冒泡）</td></tr><tr><td><code>mouseleave</code></td><td>鼠标离开元素（不冒泡）</td></tr><tr><td><code>wheel</code></td><td>鼠标滚轮滚动</td></tr><tr><td><code>dragstart</code></td><td>拖拽开始</td></tr><tr><td><code>drag</code></td><td>拖拽中</td></tr><tr><td><code>dragend</code></td><td>拖拽结束</td></tr><tr><td><code>dragenter</code></td><td>拖拽对象进入目标区域</td></tr><tr><td><code>dragover</code></td><td>拖拽对象在目标区域内移动</td></tr><tr><td><code>dragleave</code></td><td>拖拽对象离开目标区域</td></tr><tr><td><code>drop</code></td><td>在目标区域释放拖拽对象</td></tr></tbody></table><hr><h2 id="📝-二、表单与输入事件" tabindex="-1">📝 二、表单与输入事件 <a class="header-anchor" href="#📝-二、表单与输入事件" aria-label="Permalink to &quot;📝 二、表单与输入事件&quot;">​</a></h2><table tabindex="0"><thead><tr><th>事件名</th><th>描述</th></tr></thead><tbody><tr><td><code>input</code></td><td><code>&lt;input&gt;</code>、<code>&lt;textarea&gt;</code> 内容变化时实时触发</td></tr><tr><td><code>change</code></td><td>表单元素值改变并失去焦点后触发</td></tr><tr><td><code>focus</code></td><td>元素获得焦点</td></tr><tr><td><code>blur</code></td><td>元素失去焦点</td></tr><tr><td><code>submit</code></td><td>表单提交（绑定在 <code>&lt;form&gt;</code> 上）</td></tr><tr><td><code>reset</code></td><td>表单重置</td></tr><tr><td><code>select</code></td><td>用户选中文本框中的文本时触发</td></tr><tr><td><code>invalid</code></td><td>表单验证失败时触发</td></tr><tr><td><code>beforeinput</code></td><td>在 <code>input</code> 事件之前触发（更底层）</td></tr><tr><td><code>compositionstart</code></td><td>输入法组合开始（如中文输入）</td></tr><tr><td><code>compositionupdate</code></td><td>输入法组合更新</td></tr><tr><td><code>compositionend</code></td><td>输入法组合结束</td></tr></tbody></table><hr><h2 id="🖥️-三、窗口与文档事件" tabindex="-1">🖥️ 三、窗口与文档事件 <a class="header-anchor" href="#🖥️-三、窗口与文档事件" aria-label="Permalink to &quot;🖥️ 三、窗口与文档事件&quot;">​</a></h2><table tabindex="0"><thead><tr><th>事件名</th><th>描述</th></tr></thead><tbody><tr><td><code>load</code></td><td>页面完全加载完成后触发（绑定在 <code>window</code> 或某些资源上）</td></tr><tr><td><code>DOMContentLoaded</code></td><td>HTML 文档加载完成但未加载外部资源时触发</td></tr><tr><td><code>readystatechange</code></td><td>文档加载状态变化（document.readyState）</td></tr><tr><td><code>beforeunload</code></td><td>页面即将卸载（可用于提示用户是否离开）</td></tr><tr><td><code>unload</code></td><td>页面正在卸载（用于清理操作）</td></tr><tr><td><code>resize</code></td><td>浏览器窗口大小变化</td></tr><tr><td><code>scroll</code></td><td>页面或元素滚动</td></tr><tr><td><code>hashchange</code></td><td>URL 的 hash（<code>#</code> 后面部分）发生变化</td></tr><tr><td><code>popstate</code></td><td>浏览器历史记录条目更改（如点击返回按钮）</td></tr><tr><td><code>pageshow</code> / <code>pagehide</code></td><td>页面显示/隐藏（移动端缓存页面时使用）</td></tr><tr><td><code>storage</code></td><td>同源窗口中 localStorage 被修改时触发</td></tr></tbody></table><hr><h2 id="📱-四、触摸与手势事件-移动端" tabindex="-1">📱 四、触摸与手势事件（移动端） <a class="header-anchor" href="#📱-四、触摸与手势事件-移动端" aria-label="Permalink to &quot;📱 四、触摸与手势事件（移动端）&quot;">​</a></h2><table tabindex="0"><thead><tr><th>事件名</th><th>描述</th></tr></thead><tbody><tr><td><code>touchstart</code></td><td>触摸开始</td></tr><tr><td><code>touchmove</code></td><td>触摸移动</td></tr><tr><td><code>touchend</code></td><td>触摸结束</td></tr><tr><td><code>touchcancel</code></td><td>触摸被取消（如系统中断）</td></tr><tr><td><code>gesturestart</code></td><td>手势开始（如双指缩放）</td></tr><tr><td><code>gesturechange</code></td><td>手势变化</td></tr><tr><td><code>gestureend</code></td><td>手势结束</td></tr></tbody></table><hr><h2 id="🎧-五、音频与视频事件-html5-媒体" tabindex="-1">🎧 五、音频与视频事件（HTML5 媒体） <a class="header-anchor" href="#🎧-五、音频与视频事件-html5-媒体" aria-label="Permalink to &quot;🎧 五、音频与视频事件（HTML5 媒体）&quot;">​</a></h2><table tabindex="0"><thead><tr><th>事件名</th><th>描述</th></tr></thead><tbody><tr><td><code>play</code></td><td>开始播放</td></tr><tr><td><code>playing</code></td><td>已开始播放（缓冲完成）</td></tr><tr><td><code>pause</code></td><td>暂停播放</td></tr><tr><td><code>ended</code></td><td>播放结束</td></tr><tr><td><code>seeking</code></td><td>用户开始跳转时间点</td></tr><tr><td><code>seeked</code></td><td>时间点跳转完成</td></tr><tr><td><code>timeupdate</code></td><td>当前播放时间更新</td></tr><tr><td><code>volumechange</code></td><td>音量变化</td></tr><tr><td><code>ratechange</code></td><td>播放速度变化</td></tr><tr><td><code>durationchange</code></td><td>媒体总时长变化</td></tr><tr><td><code>loadedmetadata</code></td><td>媒体元数据加载完成</td></tr><tr><td><code>loadeddata</code></td><td>媒体当前帧数据加载完成</td></tr><tr><td><code>canplay</code></td><td>媒体可以开始播放（可能需要缓冲）</td></tr><tr><td><code>canplaythrough</code></td><td>媒体可以流畅播放完全部内容</td></tr><tr><td><code>error</code></td><td>媒体加载或播放发生错误</td></tr><tr><td><code>waiting</code></td><td>需要缓冲下一帧而暂停播放</td></tr><tr><td><code>stalled</code></td><td>媒体数据无法获取</td></tr></tbody></table><hr><h2 id="🔋-六、设备与传感器事件" tabindex="-1">🔋 六、设备与传感器事件 <a class="header-anchor" href="#🔋-六、设备与传感器事件" aria-label="Permalink to &quot;🔋 六、设备与传感器事件&quot;">​</a></h2><table tabindex="0"><thead><tr><th>事件名</th><th>描述</th></tr></thead><tbody><tr><td><code>deviceorientation</code></td><td>设备方向变化（陀螺仪）</td></tr><tr><td><code>devicemotion</code></td><td>设备运动（加速度计）</td></tr><tr><td><code>orientationchange</code></td><td>屏幕方向变化（横屏/竖屏）</td></tr><tr><td><code>online</code> / <code>offline</code></td><td>网络连接状态变化（绑定在 <code>window</code>）</td></tr></tbody></table><hr><h2 id="📡-七、web-worker-相关事件" tabindex="-1">📡 七、Web Worker 相关事件 <a class="header-anchor" href="#📡-七、web-worker-相关事件" aria-label="Permalink to &quot;📡 七、Web Worker 相关事件&quot;">​</a></h2><table tabindex="0"><thead><tr><th>事件名</th><th>描述</th></tr></thead><tbody><tr><td><code>message</code></td><td>接收到 worker 发送的消息</td></tr><tr><td><code>messageerror</code></td><td>接收消息出错</td></tr><tr><td><code>error</code></td><td>worker 出现错误</td></tr><tr><td><code>close</code></td><td>worker 被关闭</td></tr></tbody></table><hr><h2 id="🧩-八、自定义与高级事件" tabindex="-1">🧩 八、自定义与高级事件 <a class="header-anchor" href="#🧩-八、自定义与高级事件" aria-label="Permalink to &quot;🧩 八、自定义与高级事件&quot;">​</a></h2><table tabindex="0"><thead><tr><th>事件名</th><th>描述</th></tr></thead><tbody><tr><td><code>CustomEvent</code></td><td>自定义事件（通过 <code>new CustomEvent()</code> 创建）</td></tr><tr><td><code>animationstart</code> / <code>animationend</code> / <code>animationiteration</code></td><td>CSS 动画事件</td></tr><tr><td><code>transitionstart</code> / <code>transitionrun</code> / <code>transitionend</code> / <code>transitioncancel</code></td><td>CSS 过渡事件</td></tr><tr><td><code>pointerdown</code> / <code>pointerup</code> / <code>pointermove</code> / <code>pointercancel</code></td><td>统一指针事件（兼容鼠标、触摸、笔）</td></tr><tr><td><code>gotpointercapture</code> / <code>lostpointercapture</code></td><td>捕获指针事件</td></tr><tr><td><code>visibilitychange</code></td><td>页面可见性变化（绑定在 <code>document</code>）</td></tr><tr><td><code>fullscreenchange</code> / <code>fullscreenerror</code></td><td>全屏模式切换事件</td></tr><tr><td><code>copy</code> / <code>cut</code> / <code>paste</code></td><td>剪贴板操作事件</td></tr><tr><td><code>abort</code></td><td>资源加载中止</td></tr><tr><td><code>error</code></td><td>加载失败通用事件（如图片、脚本）</td></tr><tr><td><code>selectstart</code> / <code>selectionchange</code></td><td>文本选择事件</td></tr><tr><td><code>securitypolicyviolation</code></td><td>CSP 违规事件</td></tr><tr><td><code>toggle</code></td><td><code>&lt;details&gt;</code> 元素展开/折叠事件</td></tr></tbody></table><hr><h2 id="🌐-九、service-worker-事件-pwa" tabindex="-1">🌐 九、Service Worker 事件（PWA） <a class="header-anchor" href="#🌐-九、service-worker-事件-pwa" aria-label="Permalink to &quot;🌐 九、Service Worker 事件（PWA）&quot;">​</a></h2><table tabindex="0"><thead><tr><th>事件名</th><th>描述</th></tr></thead><tbody><tr><td><code>install</code></td><td>Service Worker 安装时触发</td></tr><tr><td><code>activate</code></td><td>Service Worker 激活时触发</td></tr><tr><td><code>fetch</code></td><td>网络请求拦截</td></tr><tr><td><code>message</code></td><td>来自主线程的消息</td></tr><tr><td><code>push</code></td><td>接收到推送通知</td></tr><tr><td><code>notificationclick</code></td><td>用户点击通知</td></tr><tr><td><code>sync</code></td><td>后台同步触发（需注册 SyncManager）</td></tr></tbody></table><hr><h2 id="📦-十、web-storage-事件" tabindex="-1">📦 十、Web Storage 事件 <a class="header-anchor" href="#📦-十、web-storage-事件" aria-label="Permalink to &quot;📦 十、Web Storage 事件&quot;">​</a></h2><table tabindex="0"><thead><tr><th>事件名</th><th>描述</th></tr></thead><tbody><tr><td><code>storage</code></td><td>同源窗口中 <code>localStorage</code> 或 <code>sessionStorage</code> 被修改时触发</td></tr></tbody></table><hr><h2 id="🧪-十一、实验性-特定平台事件-仅供参考" tabindex="-1">🧪 十一、实验性 / 特定平台事件（仅供参考） <a class="header-anchor" href="#🧪-十一、实验性-特定平台事件-仅供参考" aria-label="Permalink to &quot;🧪 十一、实验性 / 特定平台事件（仅供参考）&quot;">​</a></h2><table tabindex="0"><thead><tr><th>事件名</th><th>描述</th></tr></thead><tbody><tr><td><code>gamepadconnected</code> / <code>gamepaddisconnected</code></td><td>游戏手柄连接/断开</td></tr><tr><td><code>beforeprint</code> / <code>afterprint</code></td><td>打印前/后触发（已被弃用）</td></tr><tr><td><code>search</code></td><td><code>&lt;input type=&quot;search&quot;&gt;</code> 中点击“搜索”按钮</td></tr><tr><td><code>beforematch</code></td><td><code>&lt;style media&gt;</code> 匹配前触发（用于性能优化）</td></tr></tbody></table><hr><h2 id="🧾-总结-你可以这样记忆" tabindex="-1">🧾 总结：你可以这样记忆 <a class="header-anchor" href="#🧾-总结-你可以这样记忆" aria-label="Permalink to &quot;🧾 总结：你可以这样记忆&quot;">​</a></h2><table tabindex="0"><thead><tr><th>类型</th><th>关键词</th></tr></thead><tbody><tr><td>基础交互</td><td>click, mouse, drag</td></tr><tr><td>表单输入</td><td>input, change, focus</td></tr><tr><td>页面生命周期</td><td>load, DOMContentLoaded, beforeunload</td></tr><tr><td>移动端</td><td>touch, gesture, orientation</td></tr><tr><td>媒体</td><td>play, pause, ended, timeupdate</td></tr><tr><td>Web API</td><td>message, storage, online/offline</td></tr><tr><td>动画过渡</td><td>animation, transition</td></tr><tr><td>自定义</td><td>CustomEvent</td></tr><tr><td>PWA</td><td>fetch, push, sync</td></tr></tbody></table><hr><p>如果你希望我帮你整理成 <strong>Markdown 表格</strong>、<strong>JSON 文件</strong> 或 <strong>事件分类卡片模板</strong>，我可以为你生成 😊</p><p>你也可以告诉我你的项目需求，我会推荐最常用的一组事件 👍</p>',45)]))}const g=d(c,[["render",a]]);export{u as __pageData,g as default};
